-- Use this file to write the SQL needed to create the basic tables for your databse
-- These tables are where you are putting the data generated by the bar as well as the relevant data from the cocktails database.

--For the scope of this I drop them before each execution
DROP TABLE IF EXISTS allTransactions;
DROP TABLE IF EXISTS allGlass;
DROP TABLE IF EXISTS drinkGlassPair;

CREATE TABLE allTransactions(
    TransactionDate TEXT NOT NULL, 
    Drink TEXT NOT NULL,
	Price REAL NOT NULL,
	Bar TEXT NOT NULL);

CREATE TABLE allGlass(
    GlassType TEXT NOT NULL,
    Stock INTEGER NOT NULL,
    Bar TEXT NOT NULL
);

CREATE TABLE drinkGlassPair(
    Drink TEXT NOT NULL,
    GlassType TEXT NOT NULL
);